import styled from 'styled-components';
import px2vw from '../../utilities/px2vw';

export const GalleryScreenContainer = styled.div`
margin-left: 20px;
margin-top: 2.5%;
margin: ${px2vw(32)};

`;

export const TitleContainer = styled.h1`
font-size: 2rem;
text-align: center;
margin-top: 50vh;

